mapper.select:
	
 	 SELECT COUNT(*)
 	 FROM D_REPLY
 	 WHERE STATUS='Y'AND BID =#{bId}

mapper.insert:
INSERT INTO 
D_BOARD 
VALUES(
	SEQ_DBNO.NEXTVAL, #{mId},#{category}, #{bTitle}, #{bType}, #{bContent}, 
	0,SYSDATE, SYSDATE, 'Y', #{bLevel} 
)

mapper.update:
UPDATE D_BOARD
SET BCOUNT = BCOUNT + 1
WHERE BID = #{bId}
