mapper.update:
	UPDATE MEMBER
	SET	STATUS = 'N'
	WHERE USER_ID=#{userId}

UPDATE MEMBER
SET POINT=#{point}
WHERE MID=#{mid}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID=#{userId}

SELECT COUNT(*)
FROM MEMBER
WHERE USER_NICKNAME=#{nickName}

SELECT USER_ID
FROM MEMBER WHERE NAME = #{userName}
Birthday = #{birthday}
EMAIL = #{email}
